import React, { useContext, useState } from 'react';
import {Context} from '../Context';
import logo from '../assets/icons/logo.svg';

const InitView = () => {
    const {players, setPlayers, setInit} = useContext(Context);
    const [player, setPlayer] = useState();

    const addPlayer = e => {
        e.preventDefault();
        setPlayers([...players, {name: player, health: 20}]);
        setPlayer('');
    }

    const startGame = () => {
        setInit(false);
    }

    return ( 
        <div className='init-view'>
            <img src={logo} alt=""/>
            <form onSubmit={addPlayer}>
                <div>
                <input value={player} onChange={e => setPlayer(e.target.value)} type="text" placeholder='Add player...'/>
                <input type="submit"/>
                </div>
                <button type='button' onClick={startGame}>Start Game</button>
            </form>
        </div>
     );
}

export default InitView;
