import React, { createContext, useState, useEffect } from "react";
import { data } from "./assets/cards";
import {shuffle, make2dArray} from './utils';

export const Context = createContext();

let cards = shuffle(data);

const ContextProvider = ({ children }) => {
  const [players, setPlayers] = useState([
    { name: "Austin", health: 18 },
    { name: "Ian", health: 23 },
    { name: "Quinn", health: 12 }
  ]);
  const [gridView, setGridView] = useState(true);
  const [grid, setGrid] = useState(make2dArray());
  const [current, setCurrent] = useState();

  useEffect(() => {
    checkGrid(grid);
  }, []);

  useEffect(() => {
    if (grid) setCurrent(grid[1][1]);
  }, [grid]);

  const checkGrid = (grid) => {
    let newGrid = [...grid];

    grid.forEach((row, i) => {
      row.forEach((card, _i) => {
        if (
          (i === 0 && _i === 1) ||
          (i === 1 && _i === 0) ||
          (i === 1 && _i === 1) ||
          (i === 1 && _i === 2) ||
          (i === 2 && _i === 1)
        ) {
          if (!newGrid[i][_i]) {
            newGrid[i][_i] = getCard();
          }
        }
      });
    });
    return newGrid;
  };

  const getCard = () => {
    const card = cards[0];
    cards = cards.slice(1, cards.length);
    return card;
  };

  return (
    <Context.Provider
      value={{ players, setPlayers, gridView, setGridView, grid, current }}
    >
      {children}
    </Context.Provider>
  );
};

export default ContextProvider;
