import React, { useState } from 'react';
import { Context } from '../Context';
import dice from '../assets/icons/dice.svg';
import qm from '../assets/icons/qm.svg';
import noresult from '../assets/icons/null.svg';
import chaos from '../assets/icons/chaos.svg';
import pw from '../assets/icons/pw.svg';

const DicePanel = () => {
    const [result, setResult] = useState();
    const [unknown, setUnknown] = useState();

    const roll = () => {
        const random = Math.floor(Math.random() * Math.floor(6)) + 1;
        console.log(random);
    }

    return ( 
        <div className='dice-panel'>
            <div onClick={roll}  onMouseOver={() => setUnknown(qm)} onMouseOut={() => setUnknown(null)} className="dice-container">
                <img src={dice} alt=""/>
            </div>
            <div className="results-container">
                <img src={unknown ? unknown : result} alt=""/>
            </div>
        </div>
     );
}

export default DicePanel;
