import React, { useState, useEffect } from "react";
import { animated, useTransition, config } from "react-spring";
import constants from "../constants";

const Player = ({ health, name, players, setPlayers, styleProp, status }) => {
  //particles
  const [pluses, setPluses] = useState([]);

  const plusTransition = useTransition(pluses, (particle, i) => i, {
    from: {
      color: "#f0ead6",
      zIndex: "9999",
      position: "absolute",
      opacity: 1,
      top: "0px",
      left: "140px",
      paddingLeft: Math.floor(Math.random() * 30)
    },
    enter: { opacity: 0, top: "0" },
    config: config.molasses
  });

  const addPlus = () => {
    let newPluses = [...pluses, "+1"];
    setPluses(newPluses);
  };

  const [minuses, setMinuses] = useState([]);

  const minusTransition = useTransition(minuses, (particle, i) => i, {
    from: {
      color: "#f0ead6",
      zIndex: "9999",
      position: "absolute",
      opacity: 1,
      top: "100px",
      paddingLeft: Math.floor(Math.random() * 30)
    },
    enter: { opacity: 0, top: "0" },
    config: config.molasses
  });

  const addMinus = () => {
    let newMinuses = [...minuses, "-1"];
    setMinuses(newMinuses);
  };

  useEffect(() => {
    if (pluses.length === 1) {
      setPluses([]);
    }
    if (minuses.length === 1) {
      setMinuses([]);
    }
  });

  //End of particles

  const decrementHealth = () => {
    if (status === constants.SETTING_UP) return;
    //particles
    addMinus();
    //end of particles
    let newPlayers = [...players];
    newPlayers.forEach(player => {
      if (player.name === name) player.health--;
    });
    setPlayers(newPlayers);
  };

  const incrementHealth = () => {
    if (status === constants.SETTING_UP) return;
    //particles
    addPlus();
    //end of particles
    let newPlayers = [...players];
    newPlayers.forEach(player => {
      if (player.name === name) player.health++;
    });
    setPlayers(newPlayers);
  };

  return (
    <animated.div style={styleProp} className="player">
      <p className="name">{name}</p>
      <div
        style={{position:'relative'}}
        className={
          "health-container " +
          (status === constants.SETTING_UP ? "disabled" : "")
        }
      >
        {/* particles */}
        {plusTransition.map(({ props, item, key }) => (
          <animated.div key={key} style={props}>
            {item}
          </animated.div>
        ))}
        {minusTransition.map(({ props, item, key }) => (
          <animated.div key={key} style={props}>
            {item}
          </animated.div>
        ))}
        {/* particles */}
        <button onClick={decrementHealth}>-</button>
        <div className="health">
          <p>{health}</p>
        </div>
        <button onClick={incrementHealth}>+</button>
      </div>
    </animated.div>
  );
};

export default Player;
