import React from "react";

const GridCard = ({ card, i, _i, planeswalk, planeswalking }) => {
  const src = card ? card.img : "back.png";

  const selectable =
    planeswalking && (i === 0 && _i === 0 || i === 0 && _i === 2 || i === 1 && _i === 1 || i === 2 && _i === 0 || i === 2 && _i === 2)
      ? "selectable"
      : "not-selectable";

  return (
    <div
      onClick={() => planeswalk(i, _i)}
      className={`grid-card ${card ? "" : "no-border"} ${selectable}`}
    >
      <div className="img-container">
        <img src={require(`../assets/images/${src}`)} alt="" />
      </div>
      {card ? (
        <div className="name">
          <p>{card.name}</p>
        </div>
      ) : null}
    </div>
  );
};

export default GridCard;
