import React, { useContext } from "react";
import { Context } from "../Context";
import Player from "./Player";
import { useMeasure } from '../hooks/useMeasure';
import {animated, useSpring} from 'react-spring';

const PlayerPanel = () => {
  const { players, setPlayers } = useContext(Context);
  const [bind, {height}] = useMeasure();

  const animation = useSpring({height});

  const mappedPlayers = players
    ? players.map(player => (
        <Player
          players={players}
          setPlayers={setPlayers}
          health={player.health}
          name={player.name}
        />
      ))
    : null;

  return (
    <animated.div style={animation} className="player-panel">
      <div className="header">
        <h1>Players</h1>
      </div>
        <div {...bind} className="player-container">
          {players.length > 0 ? (
            mappedPlayers
          ) : (
            <p className="no-players">Add players...</p>
          )}
        </div>
    </animated.div>
  );
};

export default PlayerPanel;
