import React, { useContext } from "react";
import { Context } from "../Context";
import GridCard from "./GridCard";
import { make2dArray } from '../utils';

const GridView = () => {
  const { grid, setGrid } = useContext(Context);

  const planeswalk = (i, _i) => {
    // let newGrid = [
    //   [null, null, null],
    //   [null, null, null],
    //   [null, null, null]
    // ];
    let newGrid = make2dArray();
    let a = 1 - i;
    let b = 1 - _i;
    grid.forEach((row, i) => {
      row.forEach((card, _i) => {
        let x = i + a;
        let y = _i + b;
        if (card && x < 3 && x > -1 && y < 3 && y > -1) newGrid[x][y] = card;
      });
    });
    console.log(newGrid);
  };

  const mappedCards = grid
    ? grid.map((row, i) =>
        row.map((card, _i) => (
          <GridCard planeswalk={planeswalk} i={i} _i={_i} card={card} />
        ))
      )
    : null;

  return <div className="grid-container">{mappedCards}</div>;
};

export default GridView;
