import { useState, useEffect } from "react";

const useMultiKeyPress = () => {
  const [keysPressed, setKeyPressed] = useState([]);

  const downHandler = ({ code }) => {
      const keys = [...keysPressed, code];
      setKeyPressed(keys);
  }

  const upHandler = ({ code }) => {
    const keys = keysPressed.filter(code => code !== code);
    setKeyPressed(keys);
  };

  useEffect(() => {
    window.addEventListener("keydown", downHandler);
    window.addEventListener("keyup", upHandler);
    return () => {
      window.removeEventListener("keydown", downHandler);
      window.removeEventListener("keyup", upHandler);
    };
  }, []); 

  return keysPressed;
}

export default useMultiKeyPress;
