import React, {useContext} from 'react';
import {Context} from '../Context';
import GridCard from './GridCard';

const GridView = () => {
    const {grid, setGrid} = useContext(Context);

    const planeswalk = (i, _i) => {
        let newGrid = [...grid];
        let y = 1 - i;
        let x = 1 - _i;
        newGrid.forEach((row, i) => {
            row.forEach((card, _i) => {
                newGrid[i + x][_i + y] = card;
            })
        })
    }

    const mappedCards = grid ? grid.map((row, i) => row.map((card, _i) => <GridCard planeswalk={planeswalk} i={i} _i={_i} card={card} />)) : null;

    return ( 
        <div className='grid-container'>
            {mappedCards}
        </div>
     );
}

export default GridView;
