import React, {useContext} from 'react';
import {Context} from '../Context';
import switchoverhead from '../assets/icons/switch_overhead.svg';
import switchcurrent from '../assets/icons/switch_current.svg';

const StatusView = () => {
    const {gridView, setGridView, status} = useContext(Context);

    return ( 
        <div onClick={() => setGridView(!gridView)} className='status-view'>
            {!status ? (
                <img src={gridView ? switchoverhead : switchcurrent} alt=""/>
            ): (
                <p>{status}</p>
            )}
        </div>
     );
}

export default StatusView;
