import React, { createContext, useState, useEffect } from "react";
import { data } from "./assets/cards";

export const Context = createContext();

const shuffle = array => {
  let currentIndex = array.length,
    temporaryValue,
    randomIndex;
  while (0 !== currentIndex) {
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex -= 1;
    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }
  return array;
};

let cards = shuffle(data);

const ContextProvider = ({ children }) => {
  const [players, setPlayers] = useState([
    { name: "Austin", health: 18 },
    { name: "Ian", health: 23 },
    { name: "Quinn", health: 12 }
  ]);
  const [gridView, setGridView] = useState(true);
  const [grid, setGrid] = useState([
    [null, null, null],
    [null, null, null],
    [null, null, null]
  ]);
  const [current, setCurrent] = useState();

  useEffect(() => {
    checkGrid();
  }, []);

  useEffect(() => {
    if (grid) setCurrent(grid[1][1]);
  }, [grid]);

  if(current) console.log(current);

  const checkGrid = () => {
    let newGrid = [...grid];

    grid.forEach((row, i) => {
      row.forEach((card, _i) => {
        if (
          (i === 0 && _i === 1) ||
          (i === 1 && _i === 0) ||
          (i === 1 && _i === 1) ||
          (i === 1 && _i === 2) ||
          (i === 2 && _i === 1)
        ) {
          if (!newGrid[i][_i]) {
            newGrid[i][_i] = getCard();
          }
        }
      });
    });
    setGrid(newGrid);
  };

  const getCard = () => {
    const card = cards[0];
    cards = cards.slice(1, cards.length);
    return card;
  };

  return (
    <Context.Provider
      value={{ players, setPlayers, gridView, setGridView, grid }}
    >
      {children}
    </Context.Provider>
  );
};

export default ContextProvider;
