import React, {useContext} from 'react';
import {Context} from '../Context';

const GridCard = ({card, i, _i}) => {
    const src = card ? card.img : 'back.png';
    const {planeswalk} = useContext(Context);

    return ( 
        <div onClick={planeswalk} className={`grid-card ${card ? '' : 'no-border'}`}>
            <div className='img-container'>
                <img src={require(`../assets/images/${src}`)} alt=""/>
            </div>
            {card ? <div className='name'>{card.name}</div> : null}
        </div>
     );
}

export default GridCard;
