import React, { useContext } from 'react';
import { Context } from '../Context';
import Player from './Player';

const PlayerPanel = () => {
    const { players, setPlayers } = useContext(Context);

    const mappedPlayers = players ? players.map(player => <Player setPlayer={setPlayer} health={player.health} name={player.name} />) : null;

    return ( 
        <div className='player-panel'>{mappedPlayers}</div>
     );
}

export default PlayerPanel;
